import asyncio
from metaapi_cloud_sdk import MetaApi
import pandas as pd

# Show all columns
pd.set_option('display.max_colwidth', None)

def replicate_account_ids(base_id: str, n: int) -> list:
    """
    Returns a list containing `base_id` repeated `n` times.
    
    :param base_id: The single account ID to replicate
    :param n: The number of times to replicate the ID
    :return: A list of length `n`, with each element set to `base_id`
    """
    return [base_id] * n

async def stream_multiple_accounts(token: str, account_ids: list):
    """
    Connects each account ID via MetaApi streaming, 
    waits for synchronization, and periodically prints
    the order history in a pandas DataFrame.
    """
    api = MetaApi(token)
    connections = {}

    # 1) Connect each account & wait for sync
    for account_id in account_ids:
        account = await api.metatrader_account_api.get_account(account_id)
        conn = account.get_streaming_connection()
        await conn.connect()
        await conn.wait_synchronized({'timeoutInSeconds': 60})
        connections[account_id] = conn
        print(f"Account {account_id} is now streaming in real-time.")

    print(f"\nAll {len(account_ids)} accounts are connected and synchronized.\n")

    try:
        # 2) Keep streaming. We'll log data every 5 seconds (or 1s if you wish).
        while True:
            print("Periodic check...")
            for acc_id, conn in connections.items():
                history_storage = conn.history_storage

                # Let's get all historical orders for this account
                orders = history_storage.history_orders
                
                # Convert the entire order list into a pandas DataFrame
                df = pd.DataFrame(orders)

                # Print only the last 2 rows as an example
                if not df.empty:
                    print(f"\nAccount {acc_id} â€” History Orders (DataFrame):")
                    print(df)
                else:
                    print(f"\nAccount {acc_id} has no order history yet.")

            print("-----------------------------------------------------\n")
            await asyncio.sleep(5)

    except Exception as e:
        print("Error streaming multiple accounts:", e)
    finally:
        # 3) Gracefully close all streaming connections
        await asyncio.gather(*[conn.close() for conn in connections.values()], return_exceptions=True)
        print("All streaming connections closed.")


